import { Component } from "react";

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: "ok",
      increment: 0,
      result: [],
      hasInterval: false,
    };
  }

  getImage = () => {
    let elementstring = "";
    fetch("http://localhost:5000/display")
      .then((response) => {
        return response.json();
      })
      .then((body) => {
        elementstring = body.message;
        let array = elementstring.trim().split(/\s+/);
        this.setState({ result: array });
        document.body.style.backgroundRepeat = "no-repeat";
        document.body.style.backgroundSize = "cover";
        if (!this.state.hasInterval) {
          setInterval(() => this.triggerOnInterval(), 7000);
          this.setState({ hasInterval: true });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  componentDidMount() {
    this.getImage();
  }
  triggerOnInterval = () => {
    let path =
      this.state.result[this.state.increment % this.state.result.length];
    document.body.style.backgroundImage =
      "url('http://localhost:5000/" + path + "')";
    this.setState({ increment: this.state.increment + 1 });
    console.log(this.state.result);
  };
  changeResultString = () => {};

  render() {
    return <div></div>;
  }
}
