import React, { Component } from 'react'
export default class Display extends Component {
    constructor(props) {
        super(props);
        this.getImage();
        this.state = {
            image: ""
        };
    }
    getImage = () => {
        for (let i = 0; i < 10000; i++) {
            fetch("http://localhost:5000/display")
                .then(response => {
                    return response.json()
                })
                .then(body => {
                    this.setState({ response: body.message })
                })
                .catch(error => {
                    this.setState({ response: "Error uploading" })
                });
        }
    }
    render() {
        return (
            <div style={{ backgroundImage: this.state.image, backgroundSize: "100%" }}>
            </div>
        )
    }
}
