import React, { Component } from "react";
export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: "ok",
      increment: 0,
      result: [],
    };
    this.getImage();
  }
  triggerOnInterval = () => {
    let path =
      this.state.result[this.state.increment % this.state.result.length];
    document.body.style.backgroundImage =
      "url('http://localhost:5000/" + path + "')";
    this.setState({ increment: this.state.increment + 1 });
    console.log(path);
  };
  getImage = () => {
    let elementstring = "";
    fetch("http://localhost:5000/display")
      .then((response) => {
        return response.json();
      })
      .then((body) => {
        elementstring = body.message;
        let result = elementstring.trim().split(/\s+/);
        this.setState({ result: result });
        document.body.style.backgroundRepeat = "no-repeat";
        document.body.style.backgroundSize = "cover";
        document.body.style.backgroundImage =
          "url('http://localhost:5000/" + result[0] + "')";
        setInterval(this.triggerOnInterval(result), 7000);
      })
      .catch((error) => {
        console.log("error");
      });
  };

  changeResultString = () => {};

  render() {
    return <div></div>;
  }
}
