import React, { Component } from 'react'
export default class Display extends Component {
    constructor(props) {
        super(props);
        this.getImage();
        this.state = {
            image: ""
        };
    }
    getImage = () => {
        let result = []
        let elementstring = "";
        //for (let i = 0; i < 100000; i++) {
        fetch("http://localhost:5000/display")
            .then(response => {
                return response.json()
            })
            .then(body => {
                elementstring = body.message
                result = elementstring.trim().split(/\s+/);
                console.log(result)
            })
            .catch(error => {
                console.log("error")
            });
        result.forEach(path => {
            this.setState({ image: "url('http://localhost:5000" + path + "')" })
            document.body.style.backgroundImage = this.state.image;
            console.log(this.state.image)

        });
    }
    render() {
        return (
            <div>
                <p>{this.state.image}</p>
            </div>
        )
    }
}
